var Stopper={element:null,ticks:0,id:null};Stopper.start=function(){Stopper.element=document.getElementById("stopper");Stopper.id=window.setInterval("Stopper.updateInnerHTML()",1E3)};Stopper.stop=function(){window.clearInterval(Stopper.id)};Stopper.updateInnerHTML=function(){++Stopper.ticks;Stopper.element.innerHTML=Stopper.toStringTime(Stopper.ticks)};Stopper.toStringTime=function(a){var b=a%60,c=a/60%60;return Stopper.getDoubleDigit(a/3600)+":"+Stopper.getDoubleDigit(c)+":"+Stopper.getDoubleDigit(b)}; Stopper.getDoubleDigit=function(a){intd=Math.floor(a);if(intd>=0&&intd<10)return"0"+intd.toString();return intd.toString()};