var Stopper={element:null,ticks:0,id:null};Stopper.start=function(){Stopper.element=document.getElementById("stopper");Stopper.id=window.setInterval("Stopper.updateInnerText()",1E3)};Stopper.stop=function(){window.clearInterval(Stopper.id)};Stopper.updateInnerText=function(){++Stopper.ticks;var a=Stopper.ticks%60,b=Stopper.ticks/60%60;Stopper.element.innerHTML=Stopper.getDoubleDigit(Stopper.ticks/3600)+":"+Stopper.getDoubleDigit(b)+":"+Stopper.getDoubleDigit(a)};
Stopper.getDoubleDigit=function(a){intd=Math.floor(a);if(intd>=0&&intd<10)return"0"+intd.toString();return intd.toString()};